INSERT INTO public.training_interactions VALUES ('fc2e2480-174d-4881-824c-df1ec27cfc36', 'can I get the PCI info for trx', '<p>Yes, here is the PCI (Payment Card Industry) information related to TRX (Transaction) for merchant services:</p>

<ul>
